import React, {useState} from 'react';



function Home (){


  const [searchResults, setSearchResults] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = (searchTerm) => {

      fetch('http://localhost:8001/restaurant')
      .then((response) => response.json())
      .then((data) => {

      const results = data.filter((restaurant) => restaurant.name.includes(searchTerm));
      setSearchResults(results);
      });}

    return (
        <div>

        <div  className="searchArea" >
        <form  className="d-flex" role="search">
        <input onChange={(e) => setSearchTerm(e.target.value)}
          className="form-control me-2" type="search" placeholder="Find Your Favorite Restaurants" aria-label="Search"/>
        <button onClick={()=>handleSearch(searchTerm)} className="btn btn-warning"  type="submit">Search</button>
        </form>
        </div>


        <div className="searchResults">
        <ul>
        {searchResults.map((restaurant) => (
           <div className="card" key={restaurant.id}>
            <img src={restaurant.image} className="card-img-top" alt={restaurant.name} />
            <div className="card-body">
              <h5 className="card-title">{restaurant.name}</h5>
              <p className="card-text">{restaurant.description}</p>
            </div>
            </div>
        ))}
        </ul>
        </div>

        </div>
    )
}

export default Home;
