import React, {useState} from 'react';

const Review=()=>{

    const [myReview,setmyReview]=useState("")
    const [myReviews,setmyReviews]=useState([])


    const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:8001/myReviews', {
      method: 'POST',
      body: JSON.stringify({ myReview }),
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        setmyReviews([...myReviews, data]);
        setmyReview('');
      });
  };

    return (

        <div>

             <div
             style={{width: '80%',
                    height: '780px',
            }}
             className ="reviewForm">
            <form onSubmit={handleSubmit}>
                <div style={{alignItems:'center',
            }}>
                <div className="mb-3">
                <label htmlFor="review"  className="form-label">Tell us everything !</label>
                <input style={{
                        padding:'25px',
                        marginLeft
                }} value={myReview} onChange={(e) => setmyReview(e.target.value)} type="text" placeholder='Tell us everything !' className="form-control" id="review"/>
                </div>

                <button type="submit" className="btn btn-dark">Submit</button>
                </div>

            </form>

            <ul className="reviewList">
                {myReviews.map((m) => (
                <li style={{color:'black'}} key={m.id}>{m.m}</li>
                ))}
            </ul>

             </div>
        </div>

    )
}

export default Review;
