import React, { useState} from 'react';



function Home (){


  const [searchResults, setSearchResults] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');




    const handleSearch = (searchTerm) => {


      fetch('http://localhost:8001/restaurant')
      .then((response) => response.json())
      .then((data) => {

      const results = data.filter((restaurant) => restaurant.name.toLowerCase().includes(searchTerm.toLowerCase()));
      setSearchResults(results);
      });}

      const [favorites, setFavorites] = useState([]);

      const isFavorite = (id) => favorites.includes(id);
      const addToFavorites = (id) => {
        if (isFavorite(id)) {
          setFavorites(favorites.filter((favoriteId) => favoriteId !== id));
        } else {
          setFavorites([...favorites, id]);
        }
      };

      const handleSubmit = (e) => {
      e.preventDefault();
      handleSearch(searchTerm);
      };


    return (
      <div>
        <div style={{backgroundImage:("https://www.mensjournal.com/wp-content/uploads/2019/12/sunset-main-alt.jpg?quality=82&strip=all"),}}>

        <div  className="searchArea" >

        <form onSubmit={handleSubmit} style={{
          marginTop:'320px',
        }} className="d-flex" role="search">
        <input style={{
          padding:'20px',
          marginLeft:'270px',
        }}onChange={(e) => setSearchTerm(e.target.value)}
          className="form-control me-2" type="search" placeholder="Find Your Favorite Restaurants" aria-label="Search"/>

        <button style={{width:'130px',}} className="btn btn-warning"  type="submit">Let's Go </button>
        </form>
        </div>
        </div>
        <div className="restaurant-card-container">
        <div className="ui four column grid">


       </div>
       </div>

       </div>
    )
}

export default Home;
