import React, { useEffect, useState } from 'react';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8001/restaurant')
      .then((response) => response.json())
      .then((data) => {
        const favoriteRestaurants = data.filter((restaurant) => restaurant.isFavorite === true);
        setFavorites(favoriteRestaurants);
      })
      .catch((error) => console.error(error));
  }, []);

  function toggleFavorite(id, isFav) {
    fetch(`http://localhost:8001/restaurant/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        isFavorite: !isFav,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      });
  }

  const isFavorite = (id) => favorites.find((fav) => fav.id === id);

  const addToFavorites = (id) => {
const currentFav = isFavorite(id);

    if (currentFav) {
      setFavorites(favorites.filter((favorite) => favorite.id !== id));
      toggleFavorite(id, true);
    } else {
      setFavorites([...favorites, { id }]);
      toggleFavorite(id, false);
    }
  };

  return (
    <div style={{ marginLeft: '30px' }}>
      <h1 style={{ fontSize: '30px', textAlign: 'center' }}>Favorites</h1>
      <p style={{ textAlign: 'center' }}>
        All the restaurants you liked will appear here!
      </p>
      <div className="restaurant-card-container" style={{ width: '100%' }}>
        <div className="ui four column grid"></div>
      </div>
      <div className="row">
        {favorites.map((restaurant) => (
          <div
            className="col-md-3"
            id="restaurant-card"
            key={restaurant.id}
          >
            <img
              style={{ height: 'auto', width: '100%' }}
              src={restaurant.image}
              alt={restaurant.name}
            />
            <h2>{restaurant.name}</h2>
            <p className="card-text">{restaurant.description}</p>
            <a
              style={{ fontSize: '30px' }}
              className="btn"
              onClick={() => addToFavorites(restaurant.id)}
            >
              <i
                className={`${currentFav ? 'far' : 'fas'} fa-heart`}
              ></i>
            </a>
            </div>
            ))}
          </div>
        </div>
    )
}

export default Favorites;
