import React, { useEffect, useState } from "react";

function LoginForm(handleCloseModal) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLogin, setIsLogin] = useState(true);



  const modalRef = React.useRef();

  useEffect(() => {
    const handleClickOutside = event => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        handleCloseModal();
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [handleCloseModal, modalRef]);


// const handleSubmit = async (event) => {
//     event.preventDefault();

//     const response = await fetch("http://localhost:8001/user");
//     const db = await response.json();

//     const user = db.users.find(
//       (user) => user.username === username && user.password === password
//     );

//     if (user) {
//       console.log("Login successful!");
//     } else {
//       console.error("Incorrect username or password.");
//     }
//   };


  const handleSubmit = (e) => {
    e.preventDefault();
  function fetchUsers(){
    fetch (" http://localhost:8001/user") 
    .then((resp) =>resp.json())
    .then((data)=>{

      const foundUser = data.find((user) => user.name === username && user.password === password);

      if (foundUser) {
        // Log in the user
        console.log("User logged in successfully");
      } else {
        // Show an error message to the user
        console.log("Username or password is incorrect");
      }
    });;
    }

  function signUp() {

    fetch("http://localhost:8001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        password,
        email,
      }),
    })
      .then((resp) => resp.json())
      .then((data) => {
        console.log("User signed up successfully");
      })
      .catch((error) => {
        console.error("Error signing up the user:", error);
      });
  }

    if (isLogin) {
      // Send a request to the server to log the user in
      fetchUsers();
      console.log(username, password);
    } else {
      // Send a request to the server to sign the user up
      signUp();

      console.log(username, password, confirmPassword);
    }
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div handleCloseModal={handleCloseModal} 
    style={{
      background: 'white',
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      padding: '1rem',
      borderRadius: '10px',
      boxShadow: '0px 0px 10px #ccc'
    }} ref={modalRef}>
      <form onSubmit={handleSubmit}>

        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        {!isLogin && (
          <div>
            <label>
            Confirm Password:
            <input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
          </label>
          <br />
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>
          </div>

        )}
        <br />
        <button className="btn btn-success" style={{float:'left',}}  onClick={handleSubmit} type="submit">{isLogin ? "Login" : "Sign Up"}</button> 
        <button className="btn btn-success" style={{float:'right',}} type="button" onClick={toggleForm}>
          {isLogin ? "Sign Up" : "Login"}
        </button>
      </form>
    </div>
  );
}

export default LoginForm;
