import React, {useState} from 'react';



function Home (){


  const [searchResults, setSearchResults] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = (searchTerm) => {
    fetch('http://localhost:8001/restaurant')
      .then((response) => response.json())
      .then((data) => {
        const results = data.filter((restaurant) => restaurant.name.includes(searchTerm));
        setSearchResults(results);
      });

    return (
        <div>

        <div  className="searchArea" >
        <form className="d-flex" role="search">
        <input
         onChange={(e) => setSearchTerm(e.target.value)}
          className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button onClick={(e) => handleSearch(e.target.value)} className="btn btn-warning"  type="submit">Search</button>
        </form>
        </div>


        <div className="searchResults">
        <ul>
        {searchResults.map((restaurant) => (
          <li key={restaurant.id}>{restaurant.name}</li>
        ))}
        </ul>
        </div>

        </div>
    )
}
}
export default Home;
