use
    fetch('http://localhost:8001/favorites')
    .then(response => response.json())
    .then(favorites => {
        const favoriteRestaurants = favorites.filter(restaurant => restaurant.isFavorite === true);
        console.log(favoriteRestaurants);
  })
  .catch(error => console.error(error));


    return(
        <div style={{
        marginLeft:'30px'}}>
            <h1 style={{
                fontSize:'30px',
                textAlign:'center',
            }}>Favorites</h1>
            <p style={{
                textAlign:"center"
            }}>All the restaurants you liked will appear here!</p>
            <div className="restaurant-card-container" style={{ width:'100%',}}>
                <div className="ui four column grid" >
       </div>
       </div>
        <div className="row">
          {myFavs.map((restaurant) => (
            <div
              className="col-md-3" id="restaurant-card"
              key={restaurant.id}         
            >
              <img style={{height:'auto', width:'100%',}} src={restaurant.image} alt={restaurant.name} />
              <h2>{restaurant.name}</h2>
              <p className="card-text">{restaurant.description}</p>
              <a style={{fontSize:'30px',}} className="btn" onClick={() => addToFavorites(restaurant.id)}>
              <i className={`${isFavorite(restaurant.id) ? "fas" : "far"} fa-heart`}></i>
              </a>
              </div>
            ))}
          </div>
        </div>
    )
}

export default Favorites;
