import React, {useEffect, useState} from 'react';

const Review=()=>{

    const [myReview,setmyReview]=useState("")
    const [myReviews,setmyReviews]=useState([])

    useEffect(() => {
        fetch('http://localhost:8001/myReviews')
        .then((res) => res.json())
        .then((data) => {
            setmyReviews(data);
        });
     }, []);

    const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:8001/myReviews', {
      method: 'POST',
      body: JSON.stringify({ myReview }),
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        setmyReviews([...myReviews, data]);
        setmyReview('');
      });
  };

    return (

        <div>

             <div
             style={{width: '80%',
                    height: '780px',
            }}
             className ="reviewForm">
            <form onSubmit={handleSubmit}>
                <div style={{alignItems:'center',
            }}>
                <div style={{
                        padding:'25px',
                        marginLeft:'380px',
                }} className="mb-3">
                <label htmlFor="review"  className="form-label">Leave a review with us</label>
                <input  style={{
                        padding:'20px',
                        borderRadius:'25px',
                }} value={myReview} onChange={(e) => setmyReview(e.target.value)} type="text" placeholder='Tell us everything !' className="form-control" id="review"/>
                <button style={{
                        padding:'15px',
                        marginTop:'15px',
                        marginLeft:'780px',
                        borderRadius:'5px'

                }} type="submit" className="btn btn-dark">Submit</button>
                </div>


                </div>

            </form>

            <ul className="reviewList">
                {myReviews.map((review) => (
                <li style={{textColor:'black',
            border:'1px solid #ccc',
            backgroundColor: '#f2f2f2',
            marginLeft:'150px',
            padding: '15px',
            fontSize:'20px',
            margin: '5px 0'
                }}
                key={review.id}>{review.myReview}</li>
                ))}
            </ul>

            {/* <ul className="reviewList">
                {myReviews.map((review) => (
                <li style={{color:'black'}} key={review.id}>{review.myReview}</li>
                ))}
            </ul> */}

             </div>
        </div>

    )
}

export default Review;
