import React, { useState} from 'react';



function Home (){
  const [searchResults, setSearchResults] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch=(searchTerm)=> {
      fetch('http://localhost:8001/restaurant')
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
        const results=data.filter((restaurant)=>restaurant.name.toLowerCase().includes(searchTerm.toLowerCase()));

        setSearchResults(results);
      });}

      const handleSubmit = (e) => {
      e.preventDefault();
      handleSearch(searchTerm);
      };

      const [favorites, setFavorites] = useState([]);
      const isFavorite = (id) => favorites.includes(id);
      const addToFavorites = (id) => {
        if (isFavorite(id)) {
          setFavorites(favorites.filter((favoriteId) => favoriteId !== id));
          // handleSaveFavorites();
        } else {
          setFavorites([...favorites, id]);
        }
      };

      // const handleSaveFavorites = () => {
      // fetch("http://localhost:8001/favorites", {
      //     method: "POST",
      //     headers: {
      //       "Content-Type": "application/json",
      //     },
      //     body: JSON.stringify({ favorites }),
      //   })
      //     .then((response) => response.json())
      //     .then((data) => {
      //       console.log("Favorites saved", data);
      //     })
      //     .catch((error) => {
      //       console.error("Error saving favorites", error);
      //     });
      // };

    return (

      <div>

        <div style={{backgroundImage: `url("https://www.businesslist.co.ke/img/cats/restaurants.jpg")`, height:"450px",}}>
          <div  className="searchArea" >
        <form onSubmit={handleSubmit} style={{
          marginTop:'240px',
        }} className="d-flex" role="search">
        <input style={{
          padding:'10px',
          marginLeft:'200px',
        }}onChange={(e)=>setSearchTerm(e.target.value)}
          className="form-control me-2" type="search" placeholder="Find Your Favorite Restaurants" aria-label="Search"/>

        <button style={{width:'70px',fontSize:'12px', padding:'0',}} className="btn btn-warning"  type="submit">Let's Go </button>
        </form>
        </div> 
        </div>

        <div className="restaurant-card-container" style={{ width:'100%',}}>
        <div className="ui four column grid" >
       </div>
       </div>
        <div style={{paddingLeft:'140px',}} className="row">
          {searchResults.map((restaurant) => (
            <div
              className="col-md-2" id="restaurant-card"
              key={restaurant.id}         
            >
              <img style={{height:'auto', width:'100%',}} src={restaurant.image} alt={restaurant.name} />
              <h2 style={{fontSize:'18px', paddingTop:"10px",}}>{restaurant.name}</h2>
              <p style={{fontSize:'12px', paddingTop:"10px",}} className="card-text">{restaurant.description}</p>
              <button style={{fontSize:'25px',paddingTop:'0px'}} className="btn" onClick={() => addToFavorites(restaurant.id)}>
              <i className={`${isFavorite(restaurant.id) ? "fas" : "far"} fa-heart`}></i>
              </button>
              </div>
            ))}
          </div>

       <span>
         <div style={{marginLeft:'30px',marginTop:'50px', textAlign:'center'}}>
            {/* <p>What cuisine do you want to experience ?</p> */}
              <div >
                  <button style={{margin:'5px',}} id="BBQ" type="button" className="btn btn-outline-warning"  >BBQ Restaurants</button>
                  <button style={{margin:'5px',}} id="chinese" type="button" className="btn btn-outline-warning"  >Chinese Restaurants</button>
                  <button style={{margin:'10px',}} id="drinks" type="button" className="btn btn-outline-warning" >Cocktail Bars</button>
                  <button style={{margin:'10px',}} id="fastFood" type="button" className="btn btn-outline-warning"   >Pizza Places</button>
                  <button style={{margin:'10px',}} id="soup" type="button" className="btn btn-outline-warning"  >Soup Bars</button>
                 </div>
        </div>
       </span>
       </div>

);


}

export default Home;
